sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({api, event, helpers, imports}) {
    if (api.data.gform.nowRecordFormBlob && api.data.record_page_routes.output) {
        let routes = api.data.gform.nowRecordFormBlob.tabRoutes;
        let rlRoute = [];
        if(api.data.gform.nowRecordFormBlob.detailsProps.relatedLists.length > 0) {
            rlRoute = api.data.record_page_routes.output.relatedList;
        }

        let excludedRoutes = api.data.record_page_routes.output.removeRouteFromGform;

        let filteredRoutes = excludedRoutes.length > 0 ? routes.filter((item) => {
            return excludedRoutes.indexOf(item.route) == -1;
        }) : routes;

        let routesFinal = [...filteredRoutes, ...rlRoute];
        api.setState("tabRoutes", routesFinal);
    }
}
